exports.storage = {
	get: function(name) {
		return window.localStorage.getItem(name)
	},
	getJSON: function(name) {
		var v = this.get(name)
		return v && JSON.parse(v)
	},
	set: function(name, v) {
		if (typeof v === "object") var v = JSON.stringify(v)
		window.localStorage.setItem(name, v)
	}
}